(function() {
  var Achievement, AchievementTemplate, audioPath, displaying, pending, playSound;

  AchievementTemplate = require("../templates/achievement");

  pending = [];

  displaying = false;

  audioPath = "https://cdn.gomix.com/294e834f-223f-4792-9323-5b1fa8d0402b/unlock2.mp3";

  playSound = function() {
    var audio;
    audio = new Audio(audioPath);
    audio.autoplay = true;
    return audio;
  };

  module.exports = Achievement = {
    display: function(options) {
      var achievementElement;
      if (options == null) {
        options = {};
      }
      if (displaying) {
        return pending.push(options);
      }
      if (options.title == null) {
        options.title = "Achievement Unlocked";
      }
      achievementElement = AchievementTemplate(options);
      document.body.appendChild(achievementElement);
      achievementElement.classList.add("display");
      achievementElement.appendChild(playSound());
      displaying = true;
      achievementElement.addEventListener("animationend", function(e) {
        achievementElement.remove();
        displaying = false;
        if (pending.length) {
          return Achievement.display(pending.shift());
        }
      }, false);
      return achievementElement;
    }
  };

}).call(this);

