  };

  module.exports = function(db) {
    var Files, notify, self;
    Files = db.files;
    notify = function(eventType, path) {
      return function(result) {
        self.trigger(eventType, path);
        return result;
      };
    };
    return self = Model().include(Bindable).extend({
      read: function(path) {
        return Files.get(path).then(function(result) {
          return result != null ? result.blob : void 0;
        }).then(notify("read", path));
      },
      write: function(path, blob) {
        var now;
        now = +(new Date);
        return Files.put({
          path: path,
          blob: blob,
          size: blob.size,
          type: blob.type,
          createdAt: now,
          updatedAt: now
        }).then(notify("write", path));
      },
      "delete": function(path) {
        return Files["delete"](path).then(notify("delete", path));
      },
      list: function(dir) {
        return Files.where("path").startsWith(dir).toArray().then(function(files) {
