    return {
      folder: true,
      path: path.replace(id, ""),
      relativePath: path.replace(prefix, ""),
      remotePath: path
    };
  };

  FileEntry = function(object, id, prefix, bucket) {
    var entry, path;
    path = object.Key;
    entry = {
      path: path.replace(id, ""),
      relativePath: path.replace(prefix, ""),
      remotePath: path,
      size: object.Size
    };
    entry.blob = BlobSham(entry, bucket);
    return entry;
  };

  BlobSham = function(entry, bucket) {
    var remotePath;
    remotePath = entry.remotePath;
    return {
      getURL: function() {
        return getRemote(bucket, remotePath).then(URL.createObjectURL);
      },
      readAsText: function() {
        return getRemote(bucket, remotePath).then(function(blob) {
          return blob.readAsText();
        });
      }
    };
  };

}).call(this);

