os/file-io.content:
(function() {
  module.exports = function(I, self) {
    var Modal, Observable, confirmUnsaved, currentPath, saved;
    Observable = system.Observable;
    Modal = system.UI.Modal;
    currentPath = Observable("");
    saved = Observable(true);
    confirmUnsaved = function() {
      if (saved()) {
        return Promise.resolve();
      }
      return new Promise(function(resolve, reject) {
        return Modal.confirm("You will lose unsaved progress, continue?").then(function(result) {
          if (result) {
            return resolve();
          } else {
            return reject();
          }
        });
      });
    };
    self.extend({
      currentPath: currentPath,
      saved: saved,
      "new": function() {
        if (saved()) {
          currentPath("");
          return self.newFile();
        } else {
          return confirmUnsaved().then(function() {
            saved(true);
            return self.newFile();
          });
        }
      },
      open: function() {
        return confirmUnsaved().then(function() {
          return Modal.prompt("File Path", currentPath()).then(function(newPath) {
            if (newPath) {
              return currentPath(newPath);
            } else {
              throw new Error("No path given");
            }
          }).then(function(path) {
            return system.readFile(path, true);
          }).then(function(file) {
            return self.loadFile(file);
          });
        });
      },
      save: function() {
        if (currentPath()) {
          return self.saveData().then(function(blob) {
            return system.writeFile(currentPath(), blob, true);
          }).then(function() {
            saved(true);
            return currentPath();
          });
        } else {
          return self.saveAs();
        }
      },
      saveAs: function() {
        return Modal.prompt("File Path", currentPath()).then(function(path) {
          if (path) {
            currentPath(path);
            return self.save();
          }
        });
      }
    });
    return self;
  };

}).call(this);

