test/test.content:
(function() {
  var Ajax;

  Ajax = require("../main");

  describe("Ajax", function() {
    it("should provide progress", function(done) {
      var ajax;
      ajax = Ajax();
      return ajax({
        url: "https://api.github.com/users",
        responseType: "json"
      }).progress(function(e) {
        return console.log(e);
      }).then(function(data) {
        assert(data[0].id === 1);
        return done();
      });
    });
    it("should getJSON", function(done) {
      var ajax;
      ajax = Ajax();
      return ajax({
        url: "https://api.github.com/users",
        responseType: "json"
      }).then(function(data) {
        assert(data[0].id === 1);
        assert(data[0].login === "mojombo");
        return done();
      });
    });
    it("should have complete handlers", function(done) {
      var ajax;
      ajax = Ajax();
      ajax.complete(function(e, xhr, options) {
        return done();
      });
      return ajax.getJSON("https://api.github.com/users");
    });
    return it("should work with options only", function(done) {
      var ajax;
      ajax = Ajax();
      return ajax.getJSON({
        url: "https://api.github.com/users"
      }).then(function(data) {
        assert(data[0].id === 1);
        assert(data[0].login === "mojombo");
        return done();
      });
    });
  });

}).call(this);

