        return assert.equal(result, 5);
      }).then(function() {
        return done();
      }, function(error) {
        return done(error);
      }).then(function() {
        return childWindow.close();
      });
    });
    it("should work with iframes", function(done) {
      var childWindow, iframe, postmaster;
      iframe = document.createElement('iframe');
      document.body.appendChild(iframe);
      childWindow = iframe.contentWindow;
      initWindow(childWindow);
      postmaster = Postmaster();
      postmaster.remoteTarget = function() {
        return childWindow;
      };
      return postmaster.invokeRemote("echo", 17).then(function(result) {
        return assert.equal(result, 17);
      }).then(function() {
        return done();
      }, function(error) {
        return done(error);
      }).then(function() {
        return iframe.remove();
      });
    });
    it("should handle the remote call throwing errors", function(done) {
      var childWindow, iframe, postmaster;
      iframe = document.createElement('iframe');
      document.body.appendChild(iframe);
      childWindow = iframe.contentWindow;
      initWindow(childWindow);
      postmaster = Postmaster();
      postmaster.remoteTarget = function() {
        return childWindow;
      };
      postmaster.invokeRemote("throws")["catch"](function(error) {
        return done();
      }).then(function() {
        return iframe.remove();
      });
    });
    it("should throwing a useful error when the remote doesn't define the function", function(done) {
      var childWindow, iframe, postmaster;
      iframe = document.createElement('iframe');
      document.body.appendChild(iframe);
      childWindow = iframe.contentWindow;
      initWindow(childWindow);
      postmaster = Postmaster();
      postmaster.remoteTarget = function() {
        return childWindow;
      };
      postmaster.invokeRemote("someUndefinedFunction")["catch"](function(error) {
        return done();
      }).then(function() {
        return iframe.remove();
      });
    });
    it("should handle the remote call returning failed promises", function(done) {
      var childWindow, iframe, postmaster;
      iframe = document.createElement('iframe');
      document.body.appendChild(iframe);
      childWindow = iframe.contentWindow;
      initWindow(childWindow);
      postmaster = Postmaster();
      postmaster.remoteTarget = function() {
        return childWindow;
      };
      postmaster.invokeRemote("promiseFail")["catch"](function(error) {
        return done();
      }).then(function() {
        return iframe.remove();
      });
    });
    it("should be able to go around the world", function(done) {
      var childWindow, iframe, postmaster;
      iframe = document.createElement('iframe');
      document.body.appendChild(iframe);
      childWindow = iframe.contentWindow;
      initWindow(childWindow);
      postmaster = Postmaster();
