style/window.content:
module.exports = "desktop {
  user-select: none;
  -moz-user-select: none;
  -webkit-user-select: none;
  -ms-user-select: none;
  display: block;
  flex: 1 0 auto;
}
window {
  user-select: none;
  -moz-user-select: none;
  -webkit-user-select: none;
  -ms-user-select: none;
  background-color: #d3d3d3;
  border: 4px double rgba(0,0,0,0.87);
  border-radius: 4px;
  display: flex;
  flex-direction: column;
  position: absolute;
}
window > header {
  background-color: #000080;
  border-bottom: 1px solid rgba(0,0,0,0.87);
  cursor: default;
  display: flex;
  flex: 0 0 auto;
}
window > header > icon {
  background-position: 50%;
  background-repeat: no-repeat;
  background-size: 16px;
  color: #fff;
  display: inline-block;
  flex: 0 0 auto;
  text-align: center;
  width: 0;
}
window > header > control {
  background-color: #d3d3d3;
  color: #fff;
  display: inline-block;
  flex: 0 0 auto;
  font-family: monospace;
  width: 18px;
  text-shadow: 1px 0px 0px #000, -1px 0px 0px #000, 0px -1px 0px #000, 0px 1px 0px #000, 1px 1px 0px #808080;
  text-align: center;
  border-left: 1px solid rgba(0,0,0,0.87);
}
window > header > control:first-child {
  border-left: none;
}
window > header > control.close::before {
  content: "X";
}
window > header > control.maximize::before {
  content: "â«";
}
window > header > control.minimize::before {
  content: "-";
}
window > header > control.restore {
  display: none;
}
window > header > control.restore::before {
  content: "â¬";
}
window > header > title-bar {
  color: #fff;
  display: inline-block;
  flex: 1 1 auto;
  overflow: hidden;
  padding: 0 1rem 0 6px;
  text-overflow: ellipsis;
  white-space: nowrap;
}
window > viewport {
  background-color: #fff;
  display: flex;
  height: 100%;
  overflow: auto;
  position: relative;
  z-index: 0;
}
window > viewport > * {
  margin: auto;
}
window > viewport > textarea {
  border: none;
  font-family: monospace;
  height: 100%;
  padding: 2px 4px;
  resize: none;
  width: 100%;
}
window > viewport > iframe {
  border: none;
  height: 100%;
  width: 100%;
  position: absolute;
}
window > resize {
  display: block;
  position: absolute;
}
window > resize.e,
window > resize.w {
  cursor: ew-resize;
}
window > resize.n,
window > resize.s {
  cursor: ns-resize;
}
window > resize.h {
  height: 4px;
  width: 100%;
}
window > resize.v {
  height: 100%;
  width: 4px;
}
window > resize.w {
  left: -4px;
}
window > resize.e {
  right: -4px;
}
window > resize.n {
  top: -4px;
}
window > resize.s {
  bottom: -4px;
}
window > resize.n.w {
  cursor: nw-resize;
}
window > resize.n.e {
  cursor: ne-resize;
}
window > resize.s.e {
  cursor: se-resize;
}
window > resize.s.w {
  cursor: sw-resize;
}
window > resize.n.v {
  border-bottom: 1px solid #000;
  height: 23px;
}
window > resize.s.v {
  border-top: 1px solid #000;
  height: 23px;
}
window > resize.e.h {
  border-left: 1px solid #000;
  width: 22px;
}
window > resize.w.h {
  border-right: 1px solid #000;
  width: 22px;
}
window.minimized > header {
  border-bottom: none;
}
window.minimized > header > control {
  display: none;
}
window.minimized > header > control.minimize {
  border-right: none;
  display: inline-block;
}
window.minimized > header > control.minimize::before {
  content: "+";
}
window.minimized > menu,
window.minimized > resize,
window.minimized > viewport {
  display: none;
}
window.maximized {
  border: none;
  border-radius: 0;
  height: 100%;
  width: 100%;
}
window.maximized > resize {
  display: none;
}
window.maximized > header > control {
  display: none;
}
window.maximized > header > control.restore,
window.maximized > header > control.close {
  display: inline-block;
}
frame-guard {
  display: block;
  height: 100%;
  pointer-events: none;
  position: absolute;
  width: 100%;
  z-index: 100000;
}
frame-guard.active {
  pointer-events: auto;
}
";

