style/menu.content:
module.exports = "menu {
  user-select: none;
  -moz-user-select: none;
  -webkit-user-select: none;
  -ms-user-select: none;
  background-color: #d3d3d3;
  border-bottom: 1px solid rgba(0,0,0,0.5);
  margin: 0;
}
menu:focus {
  outline: none;
}
menu.context {
  z-index: 2000;
}
menu-item {
  display: block;
  list-style-type: none;
}
menu-item.active {
  background-color: #00f;
  color: #fff;
}
menu-item > label {
  display: flex;
  padding: 0 0.25em;
  white-space: nowrap;
}
menu-item > label > * {
  flex: 1 1 auto;
}
menu-item > label > span.hotkey {
  margin-left: 1em;
}
menu-item > label > span.hotkey:empty {
  margin-left: 0;
}
menu-item > label > .decoration {
  flex: 0 1 auto;
  line-height: 1em;
  text-align: right;
  margin-left: 0.5em;
}
menu-item > label > .decoration:empty {
  margin-left: 0;
}
menu-item[disabled] {
  color: #808080;
}
menu-item[disabled].active {
  background-color: rgba(0,0,0,0.125);
}
menu.options {
  border-top: 1px solid rgba(255,255,255,0.5);
  border-left: 1px solid rgba(255,255,255,0.5);
  border-right: 1px solid rgba(0,0,0,0.5);
  box-shadow: 2px 2px 1px rgba(0,0,0,0.5);
  display: none;
  padding: 2px;
  padding-bottom: 3px;
  position: absolute;
}
menu.options.active {
  display: block;
}
menu.options > menu-item.menu {
  position: relative;
}
menu.options > menu-item.menu > menu {
  position: absolute;
  left: 100%;
  top: -3px;
  margin-left: 1px;
}
menu-item.menu.active > menu {
  background-color: #d3d3d3;
  color: #000;
  display: block;
}
menu.bar {
  display: block;
  flex: 0 0 auto;
  margin: 0;
  padding: 0;
  position: initial;
}
menu.bar > menu-item {
  display: inline-block;
}
menu.bar > menu-item > label > .decoration {
  display: none;
}
menu.bar.accelerator-active span.accelerator {
  text-decoration: underline;
}
";

